export const credentials = {
  twitter: {
    apiKey: "swbvV402ME2HyvypkTWG4Ce1u",
    apiSecret: "1YBbYLN2jvJeROTBidsx6rYNNJ6GRFGSnayjGtXrIG6aDRQt1S",
    bearerToken:
      "AAAAAAAAAAAAAAAAAAAAALtVGwEAAAAA5ZASlYjTH%2B57ME7klnhw4rYo3fA%3DoOcCxs1nMwxdjfSnL7rWCUo1aemKqzI2u5GZxNDo68NBoAO4DN",
  },
};

export const endpoints = {
  twitter:
    "https://api.twitter.com/2/tweets/search/recent?query=newsweek&tweet.fields=created_at",
  db: "https://watch-this-db-1.herokuapp.com/v1/graphql",
};

export const getPeristentVariablesQuery = JSON.stringify({
  query: `
    query MyQuery {
      newsweek_bot_constants {
        key
        value
      }
    }
  `,
});

export const updateTimeOfLastActionTaken = (newTime: string) => `
  mutation MyMutation {
    update_newsweek_bot_constants(
      where: {}
      _set: {
        key: "TIME_OF_LAST_ACTION_TAKEN"
        value: "${newTime}"
      }
    ) {
      returning {
        key
        value
      }
    }
  }
`;
